    private void jButton1MouseClicked(java.awt.event.MouseEvent evt) {//GEN-FIRST:event_jButton1MouseClicked
        MalzemeTur tur= new MalzemeTur();
        tur.setAd(jTextField1.getText().toString());
        tur.setSonKullanmaTarihi(jTextField2.getText().toString());
        Veritabani.merge(tur);
        jTextField1.setText("");
        jTextField2.setText("");
        
    }//GEN-LAST:event_jButton1MouseClicked

